plugin.callback:
/* float u = 255.0; */
float uloginv = 0.09016844005556; /* 0.5 / log(1.0 + u) */
unsigned long pos;
for (pos = 0; pos < sample_count; pos++) {
    float s = input[pos];
    float sabs = fabs(s);
    if (s >= 0)
        s =  uloginv * log(1.0 + 255.0 * sabs);
    else
        s = -uloginv * log(1.0 + 255.0 * sabs);
    buffer_write(output[pos], s);
}
