plugin.callback:
float u = 31.0;
float ufwd =  1.0 / log(1.0 + u);
float uback = 1.0 / u;
unsigned long pos;
for (pos = 0; pos < sample_count; pos++) {
    float s = input[pos];
    float sabs = fabs(s);
    sabs = ufwd * log(1.0 + u * sabs);
    sabs = floor(u * sabs) / u;
    sabs = uback * (pow(u + 1.0, sabs) - 1.0);
    if (s < 0.0)
        s = -sabs;
    else
        s = sabs;
    buffer_write(output[pos], s);
}
